import * as mongoose from "mongoose";
import { Document } from "mongoose";
import DBUtil from "../utill/dBUtil";
import CategoryModel from "../models/categoryModel";

export interface CategoryDocument extends CategoryModel, Document {}

const categorySchema = new mongoose.Schema({
  name: {
    type: String,
    trim: true,
    unique: true,
  },
  archived: {
    type: Boolean,
    default: false,
    index: true,
  },
});

export default mongoose.model<CategoryDocument>(
  DBUtil.CATEGORIES,
  categorySchema
);

export default abstract class DBUtil {
  static readonly USER = "users";

  static readonly WAREHOUSE = "warehouses";

  static readonly PRODUCT = "products";

  static readonly CATEGORIES = "categories";

  static readonly VARIANT = "variants";

  static readonly SHOP = "shops";

  static readonly LEAVE = "leaves";

  static readonly ORDER = "orders";

  static readonly DAILYPRODUCT = "dailyProducts";

  static readonly SALESPERSONSHOP = "salespersonShops";
}

import { Model } from "mongoose";
import DBConnection from "../utill/database";
import { UserDocument } from "../schemaModels/user.model";

export default abstract class Dao {
  protected mongoDB = DBConnection.connect();

  protected constructor(protected model: Model<any>) {}

  async add(item: any): Promise<any> {
    // eslint-disable-next-line new-cap
    return new this.model(item).save();
  }

  async update(updateParams: any, ref: string): Promise<any> {
    const opts = {
      runValidators: true,
      useFindAndModify: true,
    };
    return this.model.findByIdAndUpdate(ref, updateParams, opts);
  }

  async find(
    sortBy: string,
    desc: number,
    skip: number,
    limit: number,
    filter: Record<string, unknown>
  ): Promise<any[]> {
    const items = this.model.find(filter).sort({ [sortBy]: desc });

    return limit > 0 ? items.skip(skip - 1).limit(limit) : items.skip(0);
  }

  async findOne(ref: string, pop?: string[]): Promise<any> {
    if (pop) {
      const query = this.model.findById(ref);
      pop.forEach((p) => query.populate(p));
      console.log(query);
      const res = await query.exec();
      console.log(res);
      return res;
    }
    return this.model.findById(ref);
  }

  async findIdByuid(uid: string): Promise<string> {
    const user: UserDocument = await this.model.findOne({ uid });
    return user._id;
  }

  async getDocumentByObjectId(objectId: string): Promise<any> {
    return this.model.findOne({ _id: objectId }).lean();
  }

  async delete(ref: string): Promise<any> {
    return this.model.findByIdAndDelete(ref);
  }
}

import Mongoose = require("mongoose");
import config from "../config/appConfig.json";

export default class DBConnection {
  private static db: Mongoose.Connection;

  private static uri = config.development.mongo_url_testing;

  static async connect(): Promise<Mongoose.Connection> {
    if (this.db !== undefined) return this.db;

    await Mongoose.connect(this.uri, {
      useNewUrlParser: true,
      useFindAndModify: false,
      useUnifiedTopology: true,
      useCreateIndex: true,
    });

    this.db = Mongoose.connection;

    this.db.once("open", async () => {
      console.log("Connected to database");
    });
    this.db.on("error", () => {
      console.log("Error connecting to database");
    });

    return this.db;
  }

  static async disconnect() {
    if (!this.db) {
      return;
    }
    await Mongoose.disconnect();
  }
}

import { FilterQuery, Model } from "mongoose";

interface QueryHelperResult<T> {
  total: number;
  items: T[];
}

export default class QueryHelper<T extends Document> {
  constructor(
    private query: string,
    private queryFields: string[],
    private populate: string[],
    private sortBy: string,
    private filter: string,
    private page: number,
    private limit: number
  ) {}

  private static findLogic(arg: string): string {
    switch (arg) {
      case "eq":
        return "$eq";
      case "neq":
        return "$ne";
      case "in":
        return "$in";
      case "nin":
        return "$nin";
      case "gt":
        return "$gt";
      case "lt":
        return "$lt";
      default:
        return "$eq";
    }
  }

  public async generate(model: Model<any>): Promise<QueryHelperResult<T>> {
    const q = {
      $or: this.queryFields.map((field) => ({
        [field]: {
          $regex: this.query,
          $options: "i",
        },
      })),
    };
    const filter = this.filter.split(",").map((arg) => {
      const args = arg.trim().split(" ");
      const logic = QueryHelper.findLogic(args[1]);
      const value = args.splice(2);
      const operand = value.length > 1 ? value : value[0];
      return { [args[0]]: { [logic]: operand } };
    });
    const queryFilter = {
      $and: [q, ...filter],
    };
    const order = this.sortBy.startsWith("+") ? 1 : -1;
    const sortBy = this.sortBy.substr(1);
    const total = await model
      .find(queryFilter as FilterQuery<T>)
      .countDocuments()
      .exec();
    let data: any;
    if (this.limit === -1) {
      data = model
        .find(queryFilter as FilterQuery<T>)
        .sort({ [sortBy]: order });
    } else {
      data = model
        .find(queryFilter as FilterQuery<T>)
        .sort({ [sortBy]: order })
        .skip((this.page - 1) * this.limit)
        .limit(this.limit);
    }

    this.populate.forEach((p) => data.populate(p));
    const items = await data.exec();
    return {
      total,
      items,
    };
  }
}

import moment from "moment";
import Dao from "../interfaces/dao";
import DailyProduct, {
  DailyProductDocument,
} from "../schemaModels/dailyProduct.model";
import SalesProductObjectModel from "../models/salesProductObjectModel";
import UserModel from "../models/userModel";

export default class DailyProductsDAO extends Dao {
  constructor() {
    super(DailyProduct);
  }

  public async updateDailyProducts(id: string, data: any) {
    await this.model.findByIdAndUpdate(id, { dailyProducts: data });
  }

  public async getSalespersons(
    start: moment.Moment,
    end: moment.Moment
  ): Promise<DailyProductDocument[]> {
    return this.model
      .find({ createdAt: { $gte: start, $lt: end } })
      .select("salesperson")
      .populate("salesperson")
      .lean();
  }

  public async getWarehouseSalespersons(
    id: string,
    start: moment.Moment,
    end: moment.Moment
  ): Promise<DailyProductDocument[]> {
    return (
      this.model
        .find({ createdAt: { $gte: start, $lt: end } })
        .select("salesperson")
        .populate({ path: "salesperson", match: { warehouseId: id } })
        .lean()
    );
  }

  public async getSalesByDateAnalytics(
    id: string,
    start: moment.Moment,
    end: moment.Moment
  ): Promise<DailyProductDocument[]> {
    const result = await this.model
      .find({
        salesperson: id,
        createdAt: { $gte: start, $lt: end },
      })
      .populate("dailyProducts.product")
      .select(["createdAt", "dailyProducts"])
      .sort("createdAt");
    return result;
  }

  public async getSalesByDateAnalyticsDate(
    id: string,
    start: moment.Moment,
    end: moment.Moment
  ): Promise<DailyProductDocument> {
    const result = await this.model
      .findOne({
        salesperson: id,
        createdAt: { $gte: start, $lt: end },
      })
      .populate("dailyProducts.product")
      .select(["createdAt", "dailyProducts"])
      .sort("createdAt");
    return result;
  }

  public async getDailyProductsOfOneSalesperson(
    id: string
  ): Promise<DailyProductDocument> {
    const startDate = moment().startOf("day");
    const endTime = moment().endOf("day");
    return this.model
      .findOne({
        salesperson: id,
        archived: false,
        createdAt: { $gte: startDate, $lt: endTime },
      })
      .populate("dailyProducts.product")
      .sort("createdAt");
  }

  public async getAllDailyProducts(
    id: string | UserModel,
    start: moment.Moment,
    end: moment.Moment
  ): Promise<Partial<DailyProductDocument>> {
    const result = await this.model
      .findOne({
        salesperson: id,
        createdAt: { $gte: start, $lt: end },
      })
      .populate("dailyProducts.product")
      .select("dailyProducts");
    return result;
  }

  public async updateDailyProductsQuantity(
    id: string | UserModel,
    dailyProducts: SalesProductObjectModel[],
    start: moment.Moment,
    end: moment.Moment
  ): Promise<string> {
    const result = await this.model.findOneAndUpdate(
      {
        salesperson: id,
        createdAt: { $gte: start, $lt: end },
      },
      {
        dailyProducts,
      }
    );
    return result._id;
  }
}

import * as mongoose from "mongoose";
import { Document } from "mongoose";
import DBUtil from "../utill/dBUtil";
import DailyProductModel from "../models/dailyProductModel";

export interface DailyProductDocument extends DailyProductModel, Document {}

const dailyProductSchema = new mongoose.Schema(
  {
    dailyProducts: [
      {
        product: { type: mongoose.Schema.Types.ObjectId, ref: DBUtil.PRODUCT },
        quantity: {
          type: Number,
        },
        sales: {
          type: Number,
        },
      },
    ],
    salesperson: {
      type: mongoose.Schema.Types.ObjectId,
      ref: DBUtil.USER,
    },
    archived: {
      type: Boolean,
      default: false,
      index: true,
    },
  },
  { timestamps: true }
);

export default mongoose.model<DailyProductDocument>(
  DBUtil.DAILYPRODUCT,
  dailyProductSchema
);

import mongodb = require("mongodb");
import Dao from "../interfaces/dao";
import Product, { ProductDocument } from "../schemaModels/product.model";
import QueryHelper from "../utill/QueryHelper";
import ProductModel from "../models/productModel";

const { ObjectID } = mongodb;

export default class ProductDAO extends Dao {
  constructor() {
    super(Product);
  }

  public async getAll(
    filterData: Record<string, any>
  ): Promise<Record<string, any>> {
    const queryHelper = new QueryHelper(
      filterData.query,
      ["name"],
      ["categoryId"],
      filterData.sortBy,
      filterData.filter,
      filterData.page,
      filterData.limit
    );

    return queryHelper.generate(Product);
  }

  public async getOneProduct(id: string): Promise<ProductDocument> {
    return this.model.findById(id).populate("categoryId");
  }

  public async getDeletedProductByName(name: string): Promise<ProductDocument> {
    return this.model.findOne({ name, archived: true });
  }

  public async updateProduct(
    productId: string,
    productDetails: Partial<ProductModel>
  ): Promise<ProductDocument> {
    return  this.model.findByIdAndUpdate(new ObjectID(productId), productDetails);
  }

  public async deleteProduct(productId: string): Promise<void> {
    await this.model.findByIdAndUpdate(new ObjectID(productId), {
      archived: true,
    });
  }
}

