import { ErrorResponse } from "../errors/errorResponce";
import { SystemError } from "../errors/systemError";

const ID_EXISTS = "ID already exist.";
const USER_VALIDATION_FAILED = "User validation failed";
const VALIDATION_FAILED = "Validation failed";
const INVALID_UID = "uid is not valid";
const INSUFFICIENT_QUANTITY = "Insufficient Item Count";
const ARCHIVED_ITEM = "Cannot access archived items";
const NO_USER = "User Does Not Exist";

export const errorResponse = (error: any): ErrorResponse => {
  let errResponse: ErrorResponse;
  if (error.codePrefix === "auth") {
    errResponse = ErrorResponse.voidError(
      error.errorInfo.code,
      422,
      error.errorInfo.message
    );
  } else if (error instanceof SystemError) {
    errResponse = error.error;
  } else if (error.code === 11000) {
    errResponse = ErrorResponse.voidError(ID_EXISTS, 409, error.toString());
  } else if (error.message === USER_VALIDATION_FAILED) {
    errResponse = ErrorResponse.voidError(error.message, 422, error.toString());
  } else if (error.message === VALIDATION_FAILED) {
    errResponse = ErrorResponse.voidError(error.message, 422, error.toString());
  } else if (error.kind === "ObjectId") {
    errResponse = ErrorResponse.voidError(
      INVALID_UID,
      422,
      error.reason.toString()
    );
  } else if (error.message === INSUFFICIENT_QUANTITY) {
    errResponse = ErrorResponse.voidError(error.message, 400, error.toString());
  } else if (error.message === ARCHIVED_ITEM) {
    errResponse = ErrorResponse.voidError(error.message, 400, error.toString());
  } else if (error.message === NO_USER) {
    errResponse = ErrorResponse.voidError(error.message, 400, error.toString());
  } else {
    console.log(error.message);
    errResponse = ErrorResponse.voidError("Error", 422, error.toString());
  }
  return errResponse;
};

export class ErrorResponse {
  public readonly status = "error";

  constructor(
    public message: string,
    public code: number,
    public description?: string
  ) {}

  get response(): any {
    return {
      message: this.message,
      description: this.description,
    };
  }

  static voidError(message?: string, code?: number, description?: string) {
    return new ErrorResponse(
      message ?? "Unexpected error",
      code ?? 422,
      description ?? "Unexpected error in the cloud function"
    );
  }

  static notAuth(description?: string) {
    return new ErrorResponse("User not authorized", 1, description);
  }

  static notEnoughParams(description?: string) {
    return new ErrorResponse(
      "Not enough parameters are given",
      422,
      description
    );
  }

  static paramTypeError(description?: string) {
    return new ErrorResponse(
      "Parameters are not in the correct type",
      422,
      description
    );
  }

  static refNotFount(description?: string) {
    return new ErrorResponse("Reference not fount", 422, description);
  }

  static undefinedSnapshot(description?: string) {
    return new ErrorResponse("Undefined Snapshot", 4, description);
  }

  static missingRequiredData(description?: string) {
    return new ErrorResponse("Missing Required Data", 422, description);
  }

  static invalidInputData(description?: string, data?: object[]) {
    return new ErrorResponse("Invalid Input Data", 422, description);
  }
}

import { ErrorResponse } from "./errorResponce";

export class SystemError extends Error {
  constructor(private msg: string) {
    super(msg);
    Object.setPrototypeOf(this, SystemError.prototype);
  }

  get error(): ErrorResponse {
    return ErrorResponse.voidError(this.msg);
  }
}

import { Request, Response } from "express";
import Joi from "joi";
import ServiceLocator from "../../../utill/serviceLocator";
import { errorResponse } from "../../../utill/responses";
import UserModel from "../../../models/userModel";

export default class UpdateUserLocationHandler {
    public static async updateUserLocation(req: Request, res: Response): Promise<void> {
        const schema = Joi.object({
            longitude: Joi.string().required(),
            latitude: Joi.string().required()
        });
        const validation = schema.validate(req.body);
        const pathSchema = Joi.object({ salesperson: Joi.string().required() });
        const pathValidation = pathSchema.validate(req.params);
        if (pathValidation.error) {
            res.status(401).send(pathValidation.error);
            return;
        }
        if (validation.error) {
            res.status(401).send(validation.error.message);
            return;
        }
        const userId = pathValidation.value.salesperson;
        const userState: Partial<UserModel> = validation.value;
        const service = ServiceLocator.updateUserLocation;
        try {
            await service.updateUserLocation(userId, userState);
